DROP DATABASE IF EXISTS visitors;
CREATE DATABASE visitors;

USE visitors;

CREATE TABLE successful_mentees (
	id INT NOT NULL AUTO_INCREMENT, 
	first_name VARCHAR(30) NOT NULL,
	last_name VARCHAR(30) NOT NULL,
	bio TEXT NOT NULL,
	photo VARCHAR(250) NOT NULL,
	school_logo VARCHAR(250) NOT NULL,
	PRIMARY KEY(id)
);

CREATE TABLE mentors_visits (
	id INT NOT NULL AUTO_INCREMENT,
	first_name VARCHAR(30) NOT NULL,
	last_name VARCHAR(30) NOT NULL,
	highschool VARCHAR(250) NOT NULL,
	undergrad VARCHAR(250) NOT NULL,
	grad VARCHAR(250),
	employer VARCHAR(30),
	title VARCHAR(200),
	photo VARCHAR(250),
	PRIMARY KEY(id)

);

